import React, { useState } from 'react';
import './Navbar.css';
import HelpModal from './HelpModal';
import { useNavigate } from 'react-router-dom'; // Importing React Router's useNavigate hook

// Get CSRF token from cookies
const getCookie = (name) => {
  let cookieValue = null;
  if (document.cookie && document.cookie !== '') {
    const cookies = document.cookie.split(';');
    for (let cookie of cookies) {
      cookie = cookie.trim();
      if (cookie.startsWith(name + '=')) {
        cookieValue = decodeURIComponent(cookie.slice(name.length + 1));
        break;
      }
    }
  }
  return cookieValue;
};

const Navbar = () => {
  const [showHelpModal, setShowHelpModal] = useState(false);
  const navigate = useNavigate(); // Initialize useNavigate for redirection

  const handleHelp = () => {
    setShowHelpModal(true);
  };

  const handleLogout = async () => {
    try {
      const csrftoken = getCookie('csrftoken');
      console.log('Logging out, using CSRF token:', csrftoken); // Debugging line

      const res = await fetch('http://localhost:8000/logout/', {
        method: 'POST',
        credentials: 'include', // includes cookies/session
        headers: {
          'X-CSRFToken': csrftoken,
        },
      });

      console.log('Response from logout API:', res); // Debugging line

      if (res.ok) {
        // Redirect to login page after successful logout using React Router
        navigate('/login'); // This will navigate to the login page
      } else {
        alert('Logout failed!');
      }
    } catch (err) {
      console.error('Error during logout:', err); // Debugging line
      alert('Something went wrong.');
    }
  };

  return (
    <div style={{ display: 'flex', width: '100%' }}>
      <div className="sidebar"></div>
      <nav className="navbar">
        <div className="logo">Expense Tracker</div>
        <div className="navbar-buttons">
          <button onClick={handleHelp}>Help</button>
          <button onClick={handleLogout}>Logout</button>
        </div>
      </nav>

      {showHelpModal && <HelpModal onClose={() => setShowHelpModal(false)} />}
    </div>
  );
};

export default Navbar;
